mapper.delete:
delete from gl_info
where 2 > 1
and id = #{id}

mapper.select:
select
count(*)
from gl_info
where 2 > 1
and id != #{id}
and gl_biaoti = #{gl_biaoti}

select
count(*)
from ls_kc_info
where 2 > 1
and ls_mingzi = #{ls_mingzi}
and ls_kc_sj = #{ls_kc_sj}

select
count(*)
from user_info
where 2 > 1
and user_name = #{user_name}
and id != #{id}

select
count(*)
from user_info
where 2 > 1
and user_name = #{user_name}
and user_password = #{user_password}
